mapper.select:

select 

from FUN_SECTION
where SECTION_ID = #{sectionId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=SMALLINT}

mapper.delete:

delete from FUN_SECTION
where SECTION_ID = #{sectionId,jdbcType=INTEGER}
  and CITY_ID = #{cityId,jdbcType=SMALLINT}

mapper.insert:

insert into FUN_SECTION (CITY_ID, REG_ID, SECTION_CODE, 
  SECTION_NAME, VERTIFY_FLAG, FId, 
  ERP_SECTION_ID, UPDATE_TIME, SECTION_PINYIN, 
  FDD_SECTION_ID, POSITION_Y, POSITION_X
  )
values (#{cityId,jdbcType=SMALLINT}, #{regId,jdbcType=INTEGER}, #{sectionCode,jdbcType=VARCHAR}, 
  #{sectionName,jdbcType=VARCHAR}, #{vertifyFlag,jdbcType=TINYINT}, #{fid,jdbcType=INTEGER}, 
  #{erpSectionId,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}, #{sectionPinyin,jdbcType=VARCHAR}, 
  #{fddSectionId,jdbcType=INTEGER}, #{positionY,jdbcType=VARCHAR}, #{positionX,jdbcType=VARCHAR}
  )
